import React from 'react';
import Contact from '../components/Contact';

function Favourite({contacts,deleteContact, favToggle}) {
  return (
    <div className="container my-5">
      
      <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-5">
        {contacts.map((singleContact) => {
          return (
            singleContact.fav && (<Contact key={singleContact.id} contact={singleContact} deleteContact={deleteContact} favToggle={favToggle}/>)
            );
        })}
        {contacts.filter(single => single.fav).length === 0 && <h1>Sorry, No Favourite contacts to show</h1>}
      </div>
    </div>
  )
}

export default Favourite;
